import React, { useState } from 'react';
import styled from 'styled-components';
import LoginForm from './components/LoginForm';
import SignupForm from './components/SignupForm';
import ForgotPasswordForm from './components/ForgotPasswordForm';

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 20px;
`;

function App() {
  const [currentForm, setCurrentForm] = useState('login');

  const switchToSignup = () => setCurrentForm('signup');
  const switchToLogin = () => setCurrentForm('login');
  const switchToForgotPassword = () => setCurrentForm('forgotPassword');

  const renderForm = () => {
    switch (currentForm) {
      case 'signup':
        return <SignupForm onSwitchToLogin={switchToLogin} />;
      case 'forgotPassword':
        return <ForgotPasswordForm onBackToLogin={switchToLogin} />;
      default:
        return (
          <LoginForm 
            onSwitchToSignup={switchToSignup} 
            onSwitchToForgotPassword={switchToForgotPassword} 
          />
        );
    }
  };

  return (
    <AppContainer>
      {renderForm()}
    </AppContainer>
  );
}

export default App;
