import React, { useState } from 'react';
import styled from 'styled-components';

const SignupForm = ({ onSwitchToLogin }) => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
    agreeToTerms: false
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validatePassword = (password) => {
    // Password must be at least 8 characters with at least one uppercase, one lowercase, one number, and one special character
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    return passwordRegex.test(password);
  };

  const validateFullName = (name) => {
    return name.trim().length >= 2 && /^[a-zA-Z\s]+$/.test(name);
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.fullName.trim()) {
      newErrors.fullName = 'Full name is required';
    } else if (!validateFullName(formData.fullName)) {
      newErrors.fullName = 'Please enter a valid full name (letters and spaces only, minimum 2 characters)';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!validateEmail(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (!validatePassword(formData.password)) {
      newErrors.password = 'Password must be at least 8 characters with uppercase, lowercase, number, and special character';
    }

    if (!formData.confirmPassword) {
      newErrors.confirmPassword = 'Please confirm your password';
    } else if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    if (!formData.agreeToTerms) {
      newErrors.agreeToTerms = 'You must agree to the Terms & Conditions';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsLoading(true);
    setErrors({});

    try {
      // Simulate API call with security measures
      const response = await signupUser(formData);
      
      if (response.success) {
        console.log('Signup successful');
        // Redirect to login or dashboard
        onSwitchToLogin();
      } else {
        throw new Error(response.message || 'Signup failed');
      }
    } catch (error) {
      console.error('Signup error:', error);
      
      if (error.message.includes('email')) {
        setErrors({ email: error.message });
      } else {
        setErrors({ general: error.message || 'Signup failed. Please try again.' });
      }
    } finally {
      setIsLoading(false);
    }
  };

  // Simulated signup API call with security measures
  const signupUser = async (userData) => {
    // Simulate network delay
    await new Promise(resolve => setTimeout(resolve, 1500));
    
    // Simulate server-side validation
    if (userData.email === 'existing@example.com') {
      throw new Error('Email already exists. Please use a different email or try logging in.');
    }
    
    // Simulate successful signup
    return {
      success: true,
      message: 'Account created successfully!',
      user: { 
        id: Date.now(), 
        email: userData.email, 
        fullName: userData.fullName 
      }
    };
  };

  const togglePasswordVisibility = (field) => {
    if (field === 'password') {
      setShowPassword(!showPassword);
    } else {
      setShowConfirmPassword(!showConfirmPassword);
    }
  };

  return (
    <StyledWrapper>
      <form className="form" onSubmit={handleSubmit}>
        {errors.general && (
          <div className="error-message">
            {errors.general}
          </div>
        )}
        
        <div className="flex-column">
          <label>Full Name</label>
        </div>
        <div className={`inputForm ${errors.fullName ? 'error' : ''}`}>
          <svg height={20} viewBox="0 0 32 32" width={20} xmlns="http://www.w3.org/2000/svg">
            <path d="M16 15.503A5.041 5.041 0 1 0 16 5.42a5.041 5.041 0 0 0 0 10.083zm12 4.729h-2.3a8.3 8.3 0 0 0-16.6 0H4a4 4 0 0 0-4 4v2a4 4 0 0 0 4 4h24a4 4 0 0 0 4-4v-2a4 4 0 0 0-4-4z"/>
          </svg>
          <input 
            type="text" 
            className="input" 
            placeholder="Enter your full name"
            name="fullName"
            value={formData.fullName}
            onChange={handleInputChange}
            disabled={isLoading}
          />
        </div>
        {errors.fullName && <div className="field-error">{errors.fullName}</div>}
        
        <div className="flex-column">
          <label>Email</label>
        </div>
        <div className={`inputForm ${errors.email ? 'error' : ''}`}>
          <svg height={20} viewBox="0 0 32 32" width={20} xmlns="http://www.w3.org/2000/svg">
            <g id="Layer_3" data-name="Layer 3">
              <path d="m30.853 13.87a15 15 0 0 0 -29.729 4.082 15.1 15.1 0 0 0 12.876 12.918 15.6 15.6 0 0 0 2.016.13 14.85 14.85 0 0 0 7.715-2.145 1 1 0 1 0 -1.031-1.711 13.007 13.007 0 1 1 5.458-6.529 2.149 2.149 0 0 1 -4.158-.759v-10.856a1 1 0 0 0 -2 0v1.726a8 8 0 1 0 .2 10.325 4.135 4.135 0 0 0 7.83.274 15.2 15.2 0 0 0 .823-7.455zm-14.853 8.13a6 6 0 1 1 6-6 6.006 6.006 0 0 1 -6 6z" />
            </g>
          </svg>
          <input 
            type="email" 
            className="input" 
            placeholder="Enter your email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            disabled={isLoading}
          />
        </div>
        {errors.email && <div className="field-error">{errors.email}</div>}
        
        <div className="flex-column">
          <label>Password</label>
        </div>
        <div className={`inputForm ${errors.password ? 'error' : ''}`}>
          <svg height={20} viewBox="-64 0 512 512" width={20} xmlns="http://www.w3.org/2000/svg">
            <path d="m336 512h-288c-26.453125 0-48-21.523438-48-48v-224c0-26.476562 21.546875-48 48-48h288c26.453125 0 48 21.523438 48 48v224c0 26.476562-21.546875 48-48 48zm-288-288c-8.8125 0-16 7.167969-16 16v224c0 8.832031 7.1875 16 16 16h288c8.8125 0 16-7.167969 16-16v-224c0-8.832031-7.1875-16-16-16zm0 0" />
            <path d="m304 224c-8.832031 0-16-7.167969-16-16v-80c0-52.929688-43.070312-96-96-96s-96 43.070312-96 96v80c0 8.832031-7.167969 16-16 16s-16-7.167969-16-16v-80c0-70.59375 57.40625-128 128-128s128 57.40625 128 128v80c0 8.832031-7.167969 16-16 16zm0 0" />
          </svg>
          <input 
            type={showPassword ? "text" : "password"} 
            className="input" 
            placeholder="Enter your password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            disabled={isLoading}
          />
          <button 
            type="button" 
            className="password-toggle"
            onClick={() => togglePasswordVisibility('password')}
            disabled={isLoading}
          >
            {showPassword ? (
              <svg viewBox="0 0 576 512" height="1em" xmlns="http://www.w3.org/2000/svg">
                <path d="M288 32c-80.8 0-145.5 36.8-192.6 80.6C48.6 156 17.3 208 2.5 243.7c-3.3 7.9-3.3 16.7 0 24.6C17.3 304 48.6 356 95.4 399.4C142.5 443.2 207.2 480 288 480s145.5-36.8 192.6-80.6c46.8-43.5 78.1-95.4 93-131.1c3.3-7.9 3.3-16.7 0-24.6c-14.9-35.7-46.2-87.7-93-131.1C433.5 68.8 368.8 32 288 32zM144 256a144 144 0 1 1 288 0 144 144 0 1 1 -288 0zm144-64c0 35.3-28.7 64-64 64c-7.1 0-13.9-1.2-20.3-3.3c-5.5-1.8-11.9 1.6-11.7 7.4c.3 6.9 1.3 13.8 3.2 20.7c13.7 51.2 66.4 81.6 117.6 67.9s81.6-66.4 67.9-117.6c-11.1-41.5-47.8-69.4-88.6-71.1c-5.8-.2-9.2 6.1-7.4 11.7c2.1 6.4 3.3 13.2 3.3 20.3z" />
              </svg>
            ) : (
              <svg viewBox="0 0 640 512" height="1em" xmlns="http://www.w3.org/2000/svg">
                <path d="M38.8 5.1C28.4-3.1 13.3-1.2 5.1 9.2S-1.2 34.7 9.2 42.9l592 464c10.4 8.2 25.5 6.3 33.7-4.1s6.3-25.5-4.1-33.7L525.6 386.7c39.6-40.6 66.4-86.1 66.4-149.9C592 164.3 497.2 64 384 64c-50.1 0-97.4 13.7-137.9 37.4L38.8 5.1zM384 112c44.2 0 80 35.8 80 80c0 25.3-12.1 47.9-30.7 62.1L384 194.1V112zM288 236.8c0 53-43 96-96 96s-96-43-96-96s43-96 96-96s96 43 96 96zM192 144c-51.2 0-96 44.8-96 96s44.8 96 96 96s96-44.8 96-96s-44.8-96-96-96z" />
              </svg>
            )}
          </button>
        </div>
        {errors.password && <div className="field-error">{errors.password}</div>}
        
        <div className="flex-column">
          <label>Confirm Password</label>
        </div>
        <div className={`inputForm ${errors.confirmPassword ? 'error' : ''}`}>
          <svg height={20} viewBox="-64 0 512 512" width={20} xmlns="http://www.w3.org/2000/svg">
            <path d="m336 512h-288c-26.453125 0-48-21.523438-48-48v-224c0-26.476562 21.546875-48 48-48h288c26.453125 0 48 21.523438 48 48v224c0 26.476562-21.546875 48-48 48zm-288-288c-8.8125 0-16 7.167969-16 16v224c0 8.832031 7.1875 16 16 16h288c8.8125 0 16-7.167969 16-16v-224c0-8.832031-7.1875-16-16-16zm0 0" />
            <path d="m304 224c-8.832031 0-16-7.167969-16-16v-80c0-52.929688-43.070312-96-96-96s-96 43.070312-96 96v80c0 8.832031-7.167969 16-16 16s-16-7.167969-16-16v-80c0-70.59375 57.40625-128 128-128s128 57.40625 128 128v80c0 8.832031-7.167969 16-16 16zm0 0" />
          </svg>
          <input 
            type={showConfirmPassword ? "text" : "password"} 
            className="input" 
            placeholder="Confirm your password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleInputChange}
            disabled={isLoading}
          />
          <button 
            type="button" 
            className="password-toggle"
            onClick={() => togglePasswordVisibility('confirmPassword')}
            disabled={isLoading}
          >
            {showConfirmPassword ? (
              <svg viewBox="0 0 576 512" height="1em" xmlns="http://www.w3.org/2000/svg">
